mapper.select:
select SB_NO, C_NO,SB_USERID, SC_REGDATE, C_PLACE, C_PRICE,
	C_PAYMENTWAY, C_NAME
from
(
    select rownum as RNUM ,LIST.*
    from
    (
        select * from favoriteclass 
        where SB_USERID=#{userid} 
    )LIST
)	

where RNUM>#{firstRecordIndex} 
and RNUM <=#{firstRecordIndex} + #{recordCountPerPage}
