mapper.select:
select *
from
(
    select rownum as RNUM ,LIST.*
    from
    (
        select * from noticeboard
        
        order by nb_no desc
    )LIST
)

where RNUM>#{firstRecordIndex} 
and RNUM <=#{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update noticeboard
	set nb_Readcount=nb_Readcount+1
where nb_no=#{nbNo}

update noticeboard
	set NB_TITLE=#{nbTitle}, NB_CONTENT=#{nbContent}
where nb_no=#{nbNo}
